import { useState } from 'react';

import CreateOutlinedIcon from '@material-ui/icons/CreateOutlined';
import NotificationsOutlinedIcon from '@material-ui/icons/NotificationsOutlined';
import AlternateEmailIcon from '@material-ui/icons/AlternateEmail';
import PhoneIcon from '@material-ui/icons/Phone';
import PermContactCalendarIcon from '@material-ui/icons/PermContactCalendar';
import { Avatar, TextField, Button } from '@material-ui/core';

import '../src/home.less';
import {getEmail, getInitials, getPhone} from "../domain/storageControl";

const initialErrors = {
    emailError: '',
    phoneError: '',
    initialsError: ''
}

export default function Home(){

    const [initials, setInitials] = useState(getInitials());
    const [email, setEmail] = useState(getEmail());
    const [phone, setPhone] = useState(getPhone());

    const [isModify, setIsMofify] = useState(true);
    const [errors, setErrors] = useState(initialErrors);

    const [initialsInput, setInitialsInput] = useState('');
    const [emailInput, setEmailInput] = useState('');
    const [phoneInput, setPhoneInput] = useState('');

    const changeEmail = (e) => {
        setEmailInput(e.target.value);
    }
    const changeInitials = (e) => {
        setInitialsInput(e.target.value);
    }
    const changePhone = (e) => {
        setPhoneInput(e.target.value);
    }

    const validate = (): boolean => {
        // add more validation errors messages
        let newErrors = {...errors}
        newErrors.emailError = (/$^|.+@.+..+/).test(emailInput) ? '' : 'Вы неверно указали электронную почту'
        newErrors.phoneError = (/^[0-9\b]+$/).test(phoneInput) && phoneInput.length == 11 ? '': 'Вы неверно указали номер'
        newErrors.initialsError = (/[A-Z][a-z]+\s[A-Z][a-z]+/).test(initialsInput) ? '': 'Вы неверно указали имя или фамилию'

        setErrors(newErrors);

        return Object.values(newErrors).every(error => error === '')
    }

    const finisModify = () => {
        setInitialsInput('');
        setEmailInput('');
        setPhoneInput('')
        setErrors(initialErrors)
    }

    const submit = (e) => {
        e.preventDefault();
        if (validate()){
            setInitials(initialsInput);
            setEmail(emailInput);
            setPhone(phoneInput);
        }
        finisModify()
    }

    return (
        <>
            <div className='background'/>
            <div className='container'>
                <div className='top-profile'>
                    <div className='top-profile-notification'><NotificationsOutlinedIcon/></div>
                    <div className='top-profile-divider'/>
                    <div className='top-profile-avatar'><Avatar/></div>
                    <div className='top-profile-initials'>{initials}</div>
                </div>

                <div className='personal-profile-link'>Личный профиль</div>
                <div className='main-menu-link'>Главная/Личный профиль</div>
                <div className={'initials-container'}>
                    <Avatar className='avatar'/>
                    <div className='initials'>{initials}</div>
                    <div className='modify-btn'>
                        <div className='text'>Редактировать</div>
                        <CreateOutlinedIcon className='icon'/>
                    </div>
                </div>
                { isModify ?
                    <form className='info-modify-container' onSubmit={submit}>
                        <div className='info-modify'>
                            <div className='input-container'>
                                <PermContactCalendarIcon className='icon'/>
                                <TextField
                                    className='input'
                                    id='name'
                                    label='Фамилия и имя'
                                    placeholder='Укажите ваши фамилию и имя'
                                    variant='outlined'
                                    value={initialsInput}
                                    onChange={changeInitials}
                                    {...(errors.initialsError && {error: true, helperText: errors.initialsError})}
                                />
                            </div>
                            <div className='divider'/>
                            <div className='input-container'>
                                <AlternateEmailIcon className='icon'/>
                                <TextField
                                    className='input'
                                    id='email'
                                    label='E-mail'
                                    placeholder='Ivanova@mail.ru'
                                    variant='outlined'
                                    value={emailInput}
                                    onChange={changeEmail}
                                    {...(errors.emailError && {error: true, helperText: errors.emailError})}
                                />
                            </div>
                            <div className='divider'/>
                            <div className='input-container'>
                                <PhoneIcon className='icon'/>
                                <TextField
                                    className='input'
                                    id='phone'
                                    label='Номер телефона'
                                    placeholder='Укажите номер телефона'
                                    variant='outlined'
                                    value={phoneInput}
                                    onChange={changePhone}
                                    {...(errors.phoneError && {error: true, helperText: errors.phoneError})}
                                />
                            </div>
                        </div>
                        <div className='btn-container'>
                            <Button className='btn' type='submit'>Сохранить изменения</Button>
                        </div>
                    </form>
                    :
                    <div className='info-container'>
                        <div className='email-container'>
                            <AlternateEmailIcon className='icon'/>
                            <div className='email'>{email}</div>
                        </div>
                        <div className='divider'/>
                        <div className='phone-container'>
                            <PhoneIcon className='icon'/>
                            <div className='phone'>{phone}</div>
                        </div>
                    </div>
                }
            </div>
        </>
    )

}
